/**
 * @fileoverview Rule to flag use of an inefficient for loop
 * @author Evgueni Naverniouk  <evgueni.naverniouk@cumul8.com>
 * @copyright 2016 Eight Solutions Inc. All rights reserved.
 */

module.exports = function (context) {
	return {
		ForStatement: function (node) {
			if (
				node.test &&
				node.test.right &&
				node.test.right.property &&
				node.test.right.property.name === 'length'
			) {
				context.report({
					message: "Don't use '" + node.test.right.object.name + ".length' inside the for loop test expression.",
					node: node
				});
			}
		}
	};
};

module.exports.schema = [];
